import React from "react";
import { NavLink, Link } from "react-router-dom";
import "../App.css";
import TopBar from "./TopBar";
import { useSelector } from "react-redux";
const Navbar = () => {
  const items = useSelector((state) => state.cart);
  return (
    <>
      <TopBar />
    
      <div>
        <header class="w-full mt-0 text-gray-700 bg-white border-t border-gray-100 shadow-sm body-font">
            <div class="container flex flex-col items-start justify-between p-6 mx-auto md:flex-row">
                <Link class="flex items-center mb-4 font-medium text-gray-900 title-font md:mb-0" to="/">
                    X-commerce
                </Link>
                
                <div class="items-center h-full">
                   
                    <Link to="/cart"
                        class="px-4 py-2 text-xs font-bold text-white uppercase transition-all duration-150 bg-teal-500 rounded shadow outline-none active:bg-teal-600 hover:shadow-md focus:outline-none ease">
                        Cart
                    </Link>
                </div>
            </div>
        </header>
    </div >
        <ul className="flex ess text-lg font-normal font-['Catamaran'] items-center ">
          <li className="p-4 ">
            <NavLink to="/">Home</NavLink>
          </li>
          <li className="ml-10">
            <NavLink to="/cart">
              <img className="h-8" src="./images/cart.png" alt="cart-img" />
              <button className="absolute right-22 top-12 bg-[#ff2a00] px-2 text-white rounded font-thin text-sm">
                {items.length}
              </button>
            </NavLink>
          </li>
        </ul>
      
    </>
  );
};

export default Navbar;
