import { React, useState, useEffect } from "react";
import axios from "axios";
// import { Link } from "react-router-dom";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    const fetchData = () => {
      axios
        .get("https://localhost:7166/api/Category")
        .then((res) => {
          console.log(res.data.data);
          setCategories(res.data.data);
          // console.log(categories);
        })
        .catch((err) => {
          console.log(err.message);
        });
    };
    fetchData();
  }, []);

  return (
    <div className="ml-20 mr-20 mt-3">
      <h1 className="text-center text-3xl font-bold">Categories</h1>
      <div>
        {categories.map((category) => {
          return (
            <div className="inline-flex p-4" key={category.id}>
              <div className="mt-10 max-w-xs rounded overflow-hidden shadow-lg ">
                <img
                  className="w-96"
                  src="/images/stat.jpg"
                  alt="Sunset in the mountains"
                />
                <div className="px-4 py-4">
                  <p className="font-bold text-center">{category.title}</p>
                  {/* <div className=" p-3 text-center">
                    <Link to={`/categories/${category.id}`}></Link>
                  </div> */}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Categories;
